mapper.insert:
insert 
  into schedule
  (
    sche_no
  , sche_title
  , sche_description
  , sche_date_start
  , sche_date_end
  , sche_color
  , sche_status
  , sche_status_exp
  )
  values
  (
    seq_scheno.nextval
  , #{scheTitle}
  , #{scheDescription}
  , #{scheDateStart}
  , #{scheDateEnd}
  , #{scheColor}
  , #{scheStatus}
  , #{scheStatusExp}
  )

mapper.update:
update
       schedule
   set sche_date_start = #{scheDateStart}
     , sche_date_end = #{scheDateEnd}
 where sche_no = #{scheNo}
